package BasicsProgram;

public class Pattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
for(int i=0;i<4;i++)
{
	for(int j=1;j<=4;j++)
	{
		if(j>i)
		{
		System.out.print(j+" ");
	}}
	for(int k=i;k>0;k--)
	{
		System.out.print(k+" ");
	}
	System.out.println();
}
	}

}
